import React, { useEffect, useState } from 'react';
import '../estilo/login_admin.css';

const AdminLogin = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [adminData, setClientData] = useState({});

  const handleLoginAdmin = () => {
    if (email === adminData.email && password === clientData.password) {
      window.location.href = 'http://localhost:3000/home';
    } else {
      alert('Credenciales incorrectas');
    }
  };

  useEffect(() => {
    setClientData('');
  
    if (email) {
      fetch(`http://localhost:8090/cliente/admin/${email}`)
        .then(response => response.json())
        .then(data => {
          setClientData(data);
        })
        .catch(error => {
          console.error('Error al obtener los datos del cliente:', error);
        });
    }
  }, [email]);

  return (
    <div className="container">
      <h1 className="title">Bienvenido Administrador</h1>
      <div className="form-container">
        <input type="text" placeholder="Correo electrónico" className="input" />
        <input type="password" placeholder="Contraseña" className="input" />
        <div className="button-container">
          <button className="button" onClick={handleLoginAdmin}>Iniciar Sesión</button>
        </div>
      </div>
    </div>
  );
};

export default AdminLogin;
