import React, { useState } from 'react';
import '../estilo/Register_cliente.css';

function RegistrationPage() {
  const [formData, setFormData] = useState({
    name: "",
    last_name: "",
    username: "",
    password: "",
    email: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const Register = () => {
    fetch(`http://localhost:8090/cliente/email/${email}`)
    .then(response => response.json())
    .then(data => {
      console.log('Email en uso:', data);
      alert('El email ya pertenece a una cuenta');
    })
    .catch(error => {
      if (error) {
        fetch(`http://localhost:8090/cliente/email/${username}`)
        .then(data => {
          console.log('Username en uso:', data);
          alert('El email ya pertenece a una cuenta');
        })
        .catch(error => {
          if (error) {
            fetch('http://localhost:8090/cliente', {
            method: 'POST',
            headers: {
            'Content-Type': 'application/json'
            },
            body: JSON.stringify(formData)
            })
            .then(response => response.json())
            .then(data => {
              console.log('Registro exitoso:', data);
              window.location.href = 'http://localhost:3000/home';
            })
            .catch(error => {
              console.error('Error en el registro:', error);
              alert('Credenciales incorrectas');
            });
          }
        });
      }
    });
  };

  return (
    <div className="registration-container">
      <h2>Registro</h2>
      <form onSubmit={Register} className="registration-form">
        <label>
          Nombre:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Apellido:
          <input
            type="text"
            name="last_name"
            value={formData.last_name}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Nombre de usuario:
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Contrase√±a:
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <button type="submit">Registrarse</button>
      </form>
    </div>
  );
}

export default RegistrationPage;
