import React, { useState } from 'react';
import '../estilo/Register_cliente.css';

function RegistrationPage() {
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');

  const [formData, setFormData] = useState({
    name: "",
    last_name: "",
    username: "",
    password: "",
    email: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const Register = () => {
    fetch(`http://localhost:8090/cliente/email/${formData.email}`)
    .then(responseEmail => responseEmail.json())
    .then(data => {
      setEmail(data.email);
    })
    fetch(`http://localhost:8090/cliente/username/${formData.username}`)
    .then(responseUsername => responseUsername.json())
    .then(data => {
      setUsername(data.username);
    })

    if (formData.email === email) {
      alert('Credenciales incorrectas');
    }
    fetch('http://localhost:8090/cliente', {
    method: 'POST',
    headers: {
    'Content-Type': 'application/json'
    },
    body: JSON.stringify(formData)
    })
    .then(response => response.json())
    .then(data => {
      console.log('Registro exitoso:', data);
      window.location.href = 'http://localhost:3000/home';
    })
    .catch(error => {
      console.error('Error en el registro:', error);
      alert('Credenciales incorrectas');
    });
  };

  return (
    <div className="registration-container">
      <h2>Registro</h2>
      <form onSubmit={Register} className="registration-form">
        <label>
          Nombre:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Apellido:
          <input
            type="text"
            name="last_name"
            value={formData.last_name}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Nombre de usuario:
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Contrase√±a:
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <button type="submit">Registrarse</button>
      </form>
    </div>
  );
}

export default RegistrationPage;
