import React, { useState } from 'react';
import './Register_cliente.css'

function RegistrationPage() {
  const [formData, setFormData] = useState({
    id: 1,
    name: 'Tomi',
    last_name: 'Garbe',
    user_name: 'tomig',
    password: '12345',
    email: 'tomig@123'
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };
  
  const Register = () => {
    useEffect(() => {
      setClientData('');
    
      if (email) {
        fetch('http://localhost:5001/api/cliente/email', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ email: email })
        })
          .then(response => response.json())
          .then(data => {
            setClientData(data);
          })
          .catch(error => {
            console.error('Error al obtener los datos del cliente:', error);
          });
      }
    }, [email]);
    /*fetch('http://localhost:5001/api/cliente', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(formData)
    })
    .then(response => response.json())
    .then(data => {
      console.log('Registro exitoso:', data);
      // window.location.href = 'http://localhost:3000/home';
    })
    .catch(error => {
      console.error('Error en el registro:', error);
      // alert('Credenciales incorrectas');
    });*/
  };

  /*const Register = () => {
    fetch('http://localhost:5001/api/cliente', formData)
    .then(response => response.json())
    .then(data => {
      console.log('Registro exitoso:', data);
      // window.location.href = 'http://localhost:3000/home';
    })
    .catch(error => {
      console.error('Error en el registro:', error);
      // alert('Credenciales incorrectas');
    });
  };*/

  /*const Register = () => {
    fetch(`http://localhost:5001/api/cliente/${formData}`)
    .then(response => response.json())
    .then(data => {
      console.log('Registro exitoso:', data);
      // window.location.href = 'http://localhost:3000/home';
    })
    .catch(error => {
      console.error('Error en el registro:', error);
      // alert('Credenciales incorrectas');
    });
  };*/

  /*async function login(username, password) {
  return await fetch('http://localhost:8090/login', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
	body: JSON.stringify({"username": username, "password":password})
  })
    .then(response => {
      if (response.status == 400 || response.status == 401)
      {
        return {"user_id": -1}
      }
      return response.json()
    })
    .then(response => {
      Cookie.set("user_id", response.user_id, {path: '/'})
      Cookie.set("username", username, {path: '/login'})
    })
 }
{
  "id": 1,
  "name": "Tomi",
  "last_name": "Garbe",
  "username": "tomig",
  "password": "12345",
  "email": "tomig@123"
}
*/ 

  return (
    <div className="registration-container">
      <h2>Registro</h2>
      <form onSubmit={Register} className="registration-form">
        <label>
          Nombre:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Apellido:
          <input
            type="text"
            name="last_name"
            value={formData.last_name}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Nombre de usuario:
          <input
            type="text"
            name="user_name"
            value={formData.user_name}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Contraseña:
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <button type="submit" onClick={Register}>Registrarse</button>
      </form>
    </div>
  );
}

export default RegistrationPage;
